            child: ListTile(
              leading: Image.asset(
                item['image'],
                width: 50,
                height: 50,
              ),
              title: Text(
                item['title'],
                style: TextStyle(fontSize: 19.0),
              ),
              subtitle: Text(
                item['description'],
                maxLines: 1,
                style: TextStyle(fontSize: 17.0),
                overflow: TextOverflow.ellipsis,
              ),
              onTap: () {
                Navigator.push(
                  context,
                  MaterialPageRoute(
                    builder: (context) => DetailScreen(item: item),
                  ),
                );
              },
            ),
          );
        },
      ),
    );
  }
}

